import { HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit, TemplateRef, ViewContainerRef } from '@angular/core';
import { Router } from '@angular/router';
import { NzModalRef, NzModalService } from 'ng-zorro-antd/modal';
import { NzTableQueryParams } from 'ng-zorro-antd/table';
import { map } from 'rxjs';
import { ArticlesService } from 'src/app/services/articles.service';

interface article {
  id: number,
  title: string,
  cid: number,
  catalogName: string,
  imgurl: string,
  desc: string,
  content?: string
}
export const loadImgFail:string =
'data:image/png;base64,';

@Component({
  selector: 'app-article-list',
  templateUrl: './article-list.component.html',
  styleUrls: ['./article-list.component.css'],
  providers: [ArticlesService]
})


export class ArticleListComponent implements OnInit {
  
  tplModalButtonLoading = false;
  disabled = false;
  fallback:string = loadImgFail;

  constructor(
    private articlesService: ArticlesService,
    private modal: NzModalService,
    private router: Router,
  ) {

  }

  total = 1;
  listOfArticle: article[] = [];
  loading = true;
  pageSize = 5;
  pageIndex: number = 1;

  loadDataFromServer(pageIndex: number,
    pageSize: number,
    sortField: string | null,
    sortOrder: string | null,
    filter: Array<{ key: string; value: string[] }>
  ): void {

    this.loading = true;
    this.articlesService.getArticlesInfo(pageIndex, pageSize, sortField, sortOrder, filter)
      .pipe(map(respondData => {
        const ListData: article[] = [];
        const total: number = respondData.total;
        for (var val of respondData.data) {
          ListData.push({
            id: val['ID'],
            title: val['title'],
            cid: val['cid'],
            catalogName: val['Catalog']['name'],
            imgurl: val['img'],
            desc: val['desc'],
            // content: val['content']
          });
        }

        return { ListData, total };
      })).subscribe((data) => {
        this.loading = false;
        this.total = data.total;
        this.listOfArticle = data.ListData;
      }
      );
  }

  onQueryParamsChange(params: NzTableQueryParams): void {
    const { pageSize, pageIndex, sort, filter } = params;
    const currentSort = sort.find(item => item.value !== null);
    const sortField = (currentSort && currentSort.key) || null;
    const sortOrder = (currentSort && currentSort.value) || null;
    this.loadDataFromServer(pageIndex, pageSize, sortField, sortOrder, filter);
  }


  onbtnSearchClicked() {
    // this.loadDataFromServer(this.pageIndex, this.pageSize, null, null, []);
  }

  onbtnEditClicked(id: number,title:string ,catalogName: string,img:string ,desc: string,) {
    this.router.navigate(['blogadmin/addArticle'],{queryParams:{
      'id':id,
      'title':title,
      'name':catalogName,
      'img':img,
      'desc':desc
    }})
  }

  onbtnDeleteClicked(id: number) {
    const ref: NzModalRef = this.modal.confirm(
      {
        nzTitle: '提示',
        nzContent: '确定删除吗',
        nzMaskClosable: false,
        nzOkDanger: true,
        nzOnOk: () => new Promise((resolve, reject) => {
          ref.updateConfig({ nzCancelDisabled: true })
          setTimeout(resolve, 1000)
        }).then(() => {
          this.articlesService.deleteArticle(id).subscribe(
            {
              next: (respondData) => {
                // console.log(respondData)
                if (respondData.message === 'OK') {
                  this.modal.success({ nzTitle: '删除成功' })
                  this.listOfArticle = this.listOfArticle.filter(d => d.id !== id)
                }
              },
              error: (error: HttpErrorResponse) => {
                // console.log(error)
                this.modal.error({ nzTitle: '删除失败', nzContent: error.statusText })
              }
            }
          )
        })
      });
  }


  ngOnInit(): void {
    this.loadDataFromServer(this.pageIndex, this.pageSize, null, null, []);
  }
}


